import React from "react";
import { StyleSheet, View, Text, Image, ImageBackground } from "react-native";
import * as Font from "expo-font";
import { Surface, Button, Caption } from 'react-native-paper';
import { IconButton } from "react-native-paper";
import { useState } from "react";
import AppLoading from 'expo-app-loading';

const getFonts = () => Font.loadAsync({
  "blooming-elegant-hand": require("../../../assets/fonts/Blooming Elegant Hand.otf"),
  "blooming-elegant-hand-sans": require("../../../assets/fonts/Blooming Elegant Sans.otf"),
})

export default function UserInfo(props) {
    const { user } = props;
    const [fontsLoaded, setFontsLoaded] = useState(false);

    return (     
      <View>
        <Text style={styles.text1}>Mis Puntos</Text>
        <View style={styles.container}>
          <Button mode="contained" style={styles.button}>Mi Dinero</Button>
          <Button mode="contained" style={styles.button}>Mis puntos</Button>
        </View>

        <View style={styles.container}>
          <Button mode="contained" style={styles.buttonDown}>Mis Deudas</Button>
          <Button mode="contained" style={styles.buttonDown}>Mis puntos</Button>
        </View>

      </View>
    );

    if(fontsLoaded){
      
    } else {
      return (
        <AppLoading>
          startAsync={getFonts}
          onFinish={() => setFontsLoaded(true)}
        </AppLoading>
      )
    }
  }
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      flexDirection: 'row',
      justifyContent: 'center'
    },
    title: {
      fontSize: 17,
      textAlign: "center",
      top: 30,
      color: '#FFFFFF'
    },
    points: {
      fontSize: 25,
      textAlign: "center",
      fontWeight: "bold",
      top: 30,
      color: '#FFFFFF'
    },

    button: {
      height: 40,
      width: 142,
      margin: 15,
      padding: 0,
      top: 100,
      textAlign: "center",
      fontSize: 2,
    },

    buttonDown: {
      height: 40,
      width: 142,
      margin: 15,
      padding: 0,
      top: 160,
      textAlign: "center",
      fontSize: 2,
    },

    text1: {
      top: 110,
      left: 50,
      fontWeight: "bold",
    },


    surface: {
      margin: 0,
    },

    banner: {
        backgroundColor: "#15212b",
        height: "50%",
        borderBottomLeftRadius: 11,
        borderBottomRightRadius: 11,
        top: 0,
      },

      surface: {
        padding: 8,
        height: "55%",
        width: "90%",
        left: 17,
        bottom: -100,
        borderRadius: 7,
        alignItems: 'center',
        justifyContent: 'center',
        elevation: 4,
      },

      surfaceText: {
        fontSize: 15,
        fontWeight: "bold",
      },
  
  });
